//全局变量
var WINDOW_WIDTH=1024;var WINDOW_HEIGHT=768;var RADIALS=8;var MARGIN_TOP=60;var MARGIN_LEFT=30;var month=(new Date()).getMonth();var day=(new Date()).getDate()+2;const endTime=new Date(2016,month,day,18,47,52);var curShowTimeSeconds=0;var balls=[];const colors=['#33B5E5','#0099CC','#AA66CC','#9933CC','#99CCOO','#669900','#FFBB33','#FF8800','#FF4444','deeppink'];window.onload=function(){var cvs=document.getElementById("cvs");var context=cvs.getContext("2d");cvs.width=WINDOW_WIDTH;cvs.height=WINDOW_HEIGHT;curShowTimeSeconds=getCurrentShowTimeSeconds();setInterval(function(){render(context);update()},50)};function getCurrentShowTimeSeconds(){var curTime=new Date();var ret=endTime.getTime()-curTime.getTime();ret=Math.round(ret/1000);return ret>=0?ret:0}function render(cxt){cxt.clearRect(0,0,WINDOW_WIDTH,WINDOW_HEIGHT);var hours=parseInt(curShowTimeSeconds/3600);var minutes=parseInt((curShowTimeSeconds-hours*3600)/60);var seconds=curShowTimeSeconds%60;renderDigit(MARGIN_LEFT,MARGIN_TOP,parseInt(hours/10),cxt);renderDigit(MARGIN_LEFT+15*(RADIALS+1),MARGIN_TOP,parseInt(hours%10),cxt);renderDigit(MARGIN_LEFT+30*(RADIALS+1),MARGIN_TOP,10,cxt);renderDigit(MARGIN_LEFT+39*(RADIALS+1),MARGIN_TOP,parseInt(minutes/10),cxt);renderDigit(MARGIN_LEFT+54*(RADIALS+1),MARGIN_TOP,parseInt(minutes%10),cxt);renderDigit(MARGIN_LEFT+69*(RADIALS+1),MARGIN_TOP,10,cxt);renderDigit(MARGIN_LEFT+78*(RADIALS+1),MARGIN_TOP,parseInt(seconds/10),cxt);renderDigit(MARGIN_LEFT+93*(RADIALS+1),MARGIN_TOP,parseInt(seconds%10),cxt);for(var i=0;i<balls.length;i+=1){cxt.fillStyle=balls[i].color;cxt.beginPath();cxt.arc(balls[i].x,balls[i].y,RADIALS,0,2*Math.PI,true);cxt.closePath();cxt.fill()}}function update(){var nextShowTimeSeconds=getCurrentShowTimeSeconds();var nexthours=parseInt(nextShowTimeSeconds/3600);var nextminutes=parseInt((nextShowTimeSeconds-nexthours*3600)/60);var nextseconds=nextShowTimeSeconds%60;var curhours=parseInt(curShowTimeSeconds/3600);var curminutes=parseInt((curShowTimeSeconds-curhours*3600)/60);var curseconds=curShowTimeSeconds%60;if(nextseconds!=curseconds){if(parseInt(curhours/10)!=parseInt(nexthours/10)){addBalls(MARGIN_LEFT+0,MARGIN_TOP,parseInt(curhours/10))}if(parseInt(curhours%10)!=parseInt(nexthours%10)){addBalls(MARGIN_LEFT+15*(RADIALS+1),MARGIN_TOP,parseInt(curhours/10))}if(parseInt(curminutes/10)!=parseInt(nextminutes/10)){addBalls(MARGIN_LEFT+39*(RADIALS+1),MARGIN_TOP,parseInt(curminutes/10))}if(parseInt(curminutes%10)!=parseInt(nextminutes%10)){addBalls(MARGIN_LEFT+54*(RADIALS+1),MARGIN_TOP,parseInt(curminutes%10))}if(parseInt(curseconds/10)!=parseInt(nextseconds/10)){addBalls(MARGIN_LEFT+78*(RADIALS+1),MARGIN_TOP,parseInt(curseconds/10))}if(parseInt(curseconds%10)!=parseInt(nextseconds%10)){addBalls(MARGIN_LEFT+93*(RADIALS+1),MARGIN_TOP,parseInt(nextseconds/10))}curShowTimeSeconds=nextShowTimeSeconds}updateBalls()}function updateBalls(){for(var i=0;i<balls.length;i+=1){balls[i].x+=balls[i].vx;balls[i].y+=balls[i].vy;balls[i].vy+=balls[i].g;if(balls[i].y>=WINDOW_HEIGHT-RADIALS){balls[i].y=WINDOW_HEIGHT-RADIALS;balls[i].vy= -balls[i].vy*0.5}}}function addBalls(x,y,num){for(var i=0;i<digit[num].length;i+=1){for(var j=0;j<digit[num][i].length;j+=1){if(digit[num][i][j]==1){var aBall={x:x+j*2*(RADIALS+1)+(RADIALS+1),y:y+i*2*(RADIALS+1)+(RADIALS+1),g:1.5+Math.random(),vx:Math.pow(-1,Math.ceil(Math.random()*1000))*4,vy:-5,color:colors[Math.floor(Math.random()*colors.length)]};balls.push(aBall)}}}}function renderDigit(x,y,num,cxt){cxt.fillStyle="rgba(0,102,153,1.0)";for(var i=0;i<digit[num].length;i+=1){for(var j=0;j<digit[num][i].length;j+=1){if(digit[num][i][j]==1){cxt.beginPath();cxt.arc(x+j*2*(RADIALS+1)+(RADIALS+1),y+i*2*(RADIALS+1)+(RADIALS+1),RADIALS,0,2*Math.PI);cxt.closePath();cxt.fill()}}}}